import { useState } from "react";


const ar = ['apple','kiwi','orange','guava']
export const Search = () => {

    const [search,setSearch] = useState('')
    const [fil,setFil] = useState(ar);
    const [prev,setPrev] = useState(fil);

    var text;
    const change = (e) => {
     text = e.target.value ;
        setSearch(text);
    }

    const sub = () => {
        const filtered = ar.filter((item) => item.toLowerCase().includes(search.toLowerCase()));
        setFil(filtered);
        setSearch('')
    }

    return(
        <>
        <input type="text"  onChange={change} value={search}/>
        <button onClick={sub}>Submit</button>
            <ul>
                {
                    fil.map((i,k) => (
                        <li key={k}>{i}</li>
                    ))
                }
            </ul>
        <h1>{pre}</h1>
        </>
    )
}
