    const [filter,setFilter] = useState(ar);

    var txt;
    const handleChange = (e) => {
        txt = e.target.value;
        setSearch(txt);
    }
    const filtered = filter.filter((item) => item.toLowerCase().includes(search.toLowerCase()))
    return(
        <>
        <input type="text"  />
            <ul>
                {
                    ar.map((i,k) => (
                        <li key={k}>{i}</li>
                    ))
                }
            </ul>
        </>
    )
}
