import { useState } from "react";


const ar = ['apple','kiwi','orange','guava']
export const Search = () => {
    const [search,setSearch] = useState('')
    const [fil,setFil] = useState(ar);



    const sub = (e) => {
        const text = e.target.value ;
        setSearch(text);
        const filtered = ar.filter((item) => item.toLowerCase().includes(search.toLowerCase()));
        setFil(filtered)
    }
    return(
        <>
        <input type="text"  onChange={sub} value={search}/>
        <button onSubmit={sub}>Submit</button>
            <ul>
                {
                    fil.map((i,k) => (
                        <li key={k}>{i}</li>
                    ))
                }
            </ul>

        </>
    )
}
