import { useState } from "react";


const ar = ['zz','apple','kiwi','orange','guava']
const s = ['z','y',x]
export const Search = () => {

    const [search,setSearch] = useState('')
    const [fil,setFil] = useState(ar);

    const [prev,setPrev] = useState(ar);


    const change = (e) => {
    const text = e.target.value ;
        setSearch(text);
    }

    const sub = () => {
        const filtered = ar.filter((item) => item.toLowerCase().includes(search.toLowerCase()));
        setFil(filtered);
        setSearch('')
    }

    const sort = () => {
        const order = prev.sort((a,b) => a.localeCompare(b))
        setPrev(order)
    }
    return(
        <>
        <input type="text"  onChange={change} value={search}/>
        <button onClick={sub}>Submit</button>
        <button onClick={sort}>Sort</button>
            <ul>
                {
                    fil.map((i,k) => (
                        <li key={k}>{i}</li>
                    ))
                }
            </ul>
        <p>{prev}</p>
        </>
    )
}
