import { useState } from "react";

const a = ['apple','kiwi','grape']
export const Search = () => {

    const [search,setSearch] = useState('');
    const [filter,setFilter] = useState(a);

    const handleSearch = (e) => {
        const text = e.target.value;
        setSearch(text)
    }
    <>
        <input type="text" value={search} />
        {
            filter.map((i,k)=>
            <li key={k}>{i}</li>
            )
        }
    </>
}
