import {useState} from "react";

export const Search = () => {
    const [search,setSearch] = useState('');
    const [item,setItems] = useState(['apple','kiwi','guava']);
    const [filtered,setFiltered] = useState('');

    const handleSearch = (e) => {
        setSearch(e.target.value);

        const filter = item.filter((items) => search.toLocaleLowerCase().startsWith(search) );
        setFiltered(filter);
    }
    return(
        <>
            <input type="text" placeholder="Enter a fruits" 
                onChange={handleSearch}
            />
            {
                item.map( (fruit,i) => (
                    <li key={i}>{fruit}</li>
                ))
            }
            <p>{filtered}</p>
        </>
    )
}
