import React,{useState} from "react";

export const Search = () => {
    const [search,setSearch] = useState('');
    const handle = (e) => {
        setSearch(e.target.value)
    }
    return(
        <>
        <input type="text" onChange={setSearch((e) => e)} value={search} />
        <h1>{search}</h1>
        </>
    )
}
