import { useState } from "react";

const val = [
    {
        name:'apple',
        img :'https://images.pexels.com/photos/206959/pexels-photo-206959.jpeg?cs=srgb&dl=pexels-pixabay-206959.jpg&fm=jpg'
    },
    {
        name:'kiwi'
    },
    {
        name:'orange'
    },
]
export const Search = () => {
    const [search,setSearch] = useState('');
    const [value,setValue] = useState(val);

    const [filters,setFilters] = useState(value);

    const handleChange = (e) => {

        const val = e.target.value;
        setSearch(val);
        console.log(`From val : ${val}`);
        console.log(`From search : ${search}`);

        const results = value.filter(item => 
            item?.name.toLowerCase().startsWith(val.toLowerCase())
        );
        setFilters(results)
    }
    return(
        <>  
            <input type="text" value={search} onChange={handleChange} />
            {
                filters.map((item,i) =>
                <li key={i}>{item.name}</li>
                )
            }
        </>
    )
}
