import { useState } from "react";

const ar = ['zz', 'apple', 'kiwi', 'orange', 'guava'];
const s = ['z', 'y', 'x', 'a', 'c', 'b'];

export const Search = () => {
    const [search, setSearch] = useState('');
    const [fil, setFil] = useState(ar);
    const [prev, setPrev] = useState(s);

    const change = (e) => {
        const text = e.target.value;
        setSearch(text);
    };
    console.log(prev)
    const sub = () => {
        const filtered = ar.filter((item) => item.toLowerCase().includes(search.toLowerCase()));
        setFil(filtered);
        setSearch(''); // Reset search input
    };

    const sort1 = () => {
        // Create a new sorted array
        const sorted = prev.sort((a, b) => a.localeCompare(b));
        setPrev(sorted);
    };

    return (
        <>
            <input type="text" onChange={change} value={search} />
            <button onClick={sub}>Submit</button>
            <button onClick={sort1}>Sort</button>
            <ul>
                {
                    fil.map((i, k) => (
                        <li key={k}>{i}</li>
                    ))
                }
            </ul>
            <p>{prev.join(', ')}</p> {/* Join the array for better display */}
        </>
    );
};
