import { useState } from "react";


const ar = ['apple','kiwi','orange','guava']
export const Search = () => {

    const [search,setSearch] = useState('')
    const [fil,setFil] = useState(ar);

    const change = (e) => {
        const text = e.target.value ;
        setSearch(text);
    }

    const sub = () => {
        const filtered = ar.filter((item) => item.toLowerCase().includes(search.toLowerCase()));
        setFil(filtered)
    }
    return(
        <>
        <input type="text"  onChange={change} value={search}/>
        <button onSubmit={sub}>Submit</button>
            <ul>
                {
                    sub.map((i,k) => (
                        <li key={k}>{i}</li>
                    ))
                }
            </ul>

        </>
    )
}
