import { useState } from "react";

const value = [
    {
        name:'apple'
    },
    {
        name:'kiwi'
    },
    {
        name:'orange'
    },
]
export const Search = () => {
    const [search,setSearch] = useState('');
    const [items,setItems] = useState(value);

    const [filters,setFilters] = (items);


    const newData = items.filter( item => item.name.toLocaleLowerCase().startsWith(search.toLocaleLowerCase()));

    setFilters(newData)
    return(
        <>
            <input type="text" value={search} 
            onChange={(e) => setSearch(e.target.value)} />
            {
                filters.map(
                    (item,i) => <li key={i}>{item.name}</li>
                )
            }
        </>
    )
}
