import { useState } from "react";

export const Search = () => {
    const [search, setSearch] = useState("");
    const [searchResults, setSearchResults] = useState([]);

    const handleInput = (e) => {
        setSearch(e.target.value);
    }
    const submit = (e) => {
        e.preventDefault();
        setSearchResultssearchResults(search)
    }
    return(
        <>
            <form onSubmit={submit}>
                <input type="text" value={search} onChange={handleInput} placeholder="Search for"/>
                <button type="submit">Submit</button>
            </form>
            <p>{searchResults}</p>
        </>
    )
}
